import { Fragment, FunctionComponent, useEffect, useState } from "react";
import { Dialog, Transition } from "@headlessui/react";
import { XMarkIcon } from "@heroicons/react/20/solid";
import { PageContent } from "../../../constants/page_content";
import { useAppSelector } from "../../../app/store/hooks";
import { backendUrl, whatsappNumber } from "../../../constants/enviroment";
import { Colors } from "../../../constants/styles";
import { AppProps } from "../../../app/utils/AppProps";
import { PhotoProvider, PhotoView } from "react-photo-view";

interface ProductDetailsProps extends AppProps {
  open: boolean;
  setOpen: (value: boolean) => void;
}

const ProductDetails: FunctionComponent<ProductDetailsProps> = ({
  open,
  setOpen,
}) => {
  const product = useAppSelector((state) => state.product.selectedProduct);
  const [imageUrl, setImageUrl] = useState("");

  const sendMessage = () => {
    window.open(
      `https://api.whatsapp.com/send?phone=${whatsappNumber}&text=Hola%2C%20quiero%20mas%20informaci√≥n%20sobre%20este%20producto%3A%20%0A${product?.title}%20S%2F%20${product?.price}`,
      "_blank"
    );
  };

  return (
    <>
      <Transition.Root show={open} as={Fragment}>
        <Dialog
          as="div"
          className="relative z-10"
          onClose={() => {
            setOpen(false);
          }}
        >
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 hidden bg-gray-500 bg-opacity-75 transition-opacity md:block" />
          </Transition.Child>

          <div className="fixed inset-0 z-10 overflow-y-auto">
            <div className="flex min-h-full items-stretch justify-center text-center md:items-center md:px-2 lg:px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 translate-y-4 md:translate-y-0 md:scale-95"
                enterTo="opacity-100 translate-y-0 md:scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 translate-y-0 md:scale-100"
                leaveTo="opacity-0 translate-y-4 md:translate-y-0 md:scale-95"
              >
                <Dialog.Panel className="flex w-full transform text-left text-base transition md:my-8 md:max-w-2xl md:px-4 lg:max-w-4xl">
                  <div className="relative flex w-full items-center overflow-hidden bg-white px-4 pt-14 pb-8 shadow-2xl sm:px-6 sm:pt-8 md:p-6 lg:p-8">
                    <button
                      type="button"
                      className="absolute top-4 right-4 text-gray-400 hover:text-gray-500 sm:top-8 sm:right-6 md:top-6 md:right-6 lg:top-8 lg:right-8"
                      onClick={() => {
                        setOpen(false);
                      }}
                    >
                      <span className="sr-only">{PageContent.close}</span>
                      <XMarkIcon className="h-6 w-6" aria-hidden="true" />
                    </button>

                    <div className="grid w-full grid-cols-1 items-start gap-y-8 gap-x-6 sm:grid-cols-12 lg:gap-x-8">
                      <div className="aspect-w-2 aspect-h-3 overflow-hidden rounded-lg bg-gray-100 sm:col-span-4 lg:col-span-5">
                        <PhotoProvider>
                          <PhotoView
                            src={
                              product?.imgUrl
                                ? `${backendUrl}${product.imgUrl}`
                                : "data:image/png;base64,"
                            }
                          >
                            <img
                              src={
                                product?.imgUrl
                                  ? `${backendUrl}${product.imgUrl}`
                                  : "data:image/png;base64,"
                              }
                              alt={product?.title}
                              className="h-60 w-full object-cover object-center cursor-pointer"
                              onClick={() => {
                                if (product?.imgUrl)
                                  setImageUrl(`${backendUrl}${product.imgUrl}`);
                              }}
                            />
                          </PhotoView>
                        </PhotoProvider>
                      </div>
                      <div className="sm:col-span-8 lg:col-span-7">
                        <h2 className="text-2xl font-bold text-gray-900 sm:pr-12">
                          {product?.title}
                        </h2>

                        <section
                          aria-labelledby="information-heading"
                          className="mt-2"
                        >
                          <h3 id="information-heading" className="sr-only">
                            {PageContent.productInformation}
                          </h3>

                          <p className="text-2xl text-gray-900">
                            {new Intl.NumberFormat("es-PE", {
                              style: "currency",
                              currency: "PEN",
                            }).format(product?.price || 0)}
                          </p>
                          <p className="text-md text-gray-900">
                            {product?.description || ""}
                          </p>
                        </section>

                        <section
                          aria-labelledby="options-heading"
                          className="mt-10"
                        >
                          {/* //TODO: Add to bag */}
                          {/* <button
                          className="mt-6 flex w-full items-center justify-center rounded-md border border-transparent bg-indigo-600 py-3 px-8 text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                        >
                          {PageContent.addToBag}
                        </button> */}
                          <button
                            className="mt-6 flex w-1/2 items-end justify-center rounded-md border border-transparent py-2 px-2 text-base font-small text-white"
                            style={{ backgroundColor: Colors[1] }}
                            onClick={sendMessage}
                          >
                            {PageContent.askForWhatsapp}
                          </button>
                        </section>
                      </div>
                    </div>
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition.Root>
      {/* {imageUrl !== "" ? (
        <Lightbox
          showTitle={false}
          onClose={() => setImageUrl("")}
          image={imageUrl}
          title="Product"
        />
      ) : null} */}
    </>
  );
};

export default ProductDetails;
